import React from "react";
import Moment from "react-moment";
import { Link } from "react-router-dom";
import DefaultProfileImg from "../images/default-profile-image.jpg";

const MessageItem = ({ date, profileImageUrl, text, imageUrl, username, removeMessage, isCorrectUser }) => (
    <li className="flex items-center spacex-3">
        <img
             className="inline-block h-10 w-10 rounded-full ring-2 ring-white"

        />
        <img  alt={username} height="100" width="100" className="timeline-image" />
        <div className="message-area">
            <Link to="/">@{username} &nbsp;</Link>
            <span>
                <Moment format="Do MMM YYYY">
                    {date}
                </Moment>
            </span>

            <p>{text}</p>
            {isCorrectUser && (
                <a onClick={removeMessage}>
                    delete
                </a>
            )}
        </div>
        {imageUrl && (
            <img src={imageUrl} height="100" width="100" />
        )}

    </li>
)

export default MessageItem;
