import { SET_CURRENT_USER } from "../actionTypes";

const DEFAULT_STATE = {
    isAuthenticated: false, // hopefully be true when logged in
    user: {}, //all the user info when logged in
};

export default (state = DEFAULT_STATE, action) => {

    switch (action.type) {
        case SET_CURRENT_USER:
            console.log("authenticated: " + !!Object.keys(action.user).length + " current user store: " + state.user.id);
            return {
                // turn empty object into false or if there are keys then true
                isAuthenticated: !!Object.keys(action.user).length,
                user: action.user
            };
        default: return state;
    }
};
