import React, { Component, useState } from "react";
import { Route, Link } from "react-router-dom";
import { connect } from "react-redux";
import { logout } from "../store/actions/auth";
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline'

const navigation = [
    { name: 'sign up', to: "/signup" },
    { name: 'log in', to: "/signin" }
]
// const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

class Navbar extends Component {
    constructor(props) {
        super(props);
        this.state = { mobileMenuOpen: false, setMobileMenuOpen: false };
    }

    // render() {
    // return <nav className="navbar navbar-expand text-rose-500">
    //     <div className="container-fluid">
    //         <Link to="/" className="navbar-brand">
    //             <img src="" alt="homepage img" />
    //         </Link>
    //     </div>

    //     <ul>
    //         <li>
    //             <Link to="/signup">sign up</Link>
    //         </li>
    //         <li>
    //             <Link to="/signin">log in</Link>
    //         </li>
    //     </ul>
    // </nav>

    logout = e => {
        e.preventDefault();
        this.props.logout();
    }

    render() {
        return <nav className="flex items-center justify-between p-6 lg:px-8 " aria-label="Global" >
            <div className="flex lg:flex-1">
                <Link to="/" className="-m-1.5 p-1.5">
                    <span className="sr-only">Your Company</span>
                    <img
                        className="h-8 w-auto"
                        src="https://tailwindui.com/img/logos/mark.svg?color=indigo&shade=600"
                        alt=""
                    />

                </Link>
            </div>
            <div className="flex lg:hidden">
                <button
                    type="button"
                    className="-m-2.5 inline-flex items-center justify-center rounded-md p-2.5 text-gray-700"
                // onClick={() => setMobileMenuOpen(true)}
                >
                    <span className="sr-only">Open main menu</span>
                    <Bars3Icon className="h-6 w-6" aria-hidden="true" />
                </button>
            </div>


            {this.props.currentUser.isAuthenticated ? (
                <div className="hidden lg:flex lg:gap-x-12">
                    <Link to={`/users/${this.props.currentUser.user.id}/messages/new`} className="text-sm font-semibold leading-6 text-gray-900">
                        {"new message"}
                    </Link>
                    <a onClick={this.logout} className="text-sm font-semibold leading-6 text-gray-900" >log out</a>
                </div>

            ) :
                (<div className="hidden lg:flex lg:gap-x-12">
                    {navigation.map((item) => (

                        <Link key={item.name} to={item.to} className="text-sm font-semibold leading-6 text-gray-900">
                            {item.name}
                        </Link>
                    ))}
                </div>)
            }
            {/* <div className="hidden lg:flex lg:flex-1 lg:justify-end">
                <a href="#" className="text-sm font-semibold leading-6 text-gray-900">
                    Log in <span aria-hidden="true">&rarr;</span>
                </a>
            </div> */}
        </nav >
    }
}

function mapStateToProps(state) {
    return {
        currentUser: state.currentUser
    }
}

export default connect(mapStateToProps, { logout })(Navbar);
