import React, { Component } from "react";
import errors from "../store/reducers/errors";
// import 

export default class AuthForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            username: "",
            password: "",
            profileImageUrl: ""
        }
    }

    handleChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    };

    handleSubmit = e => {
        e.preventDefault();
        const authType = this.props.signUp ? "signup" : "signin";
        this.props.onAuth(authType, this.state).then(() => {
            this.props.history.push("/");
        }).catch(() => {
            return;
        });
    }

    render() {
        const { email, username, password, profileImageUrl } = this.state;
        const { heading, buttonText, signUp, errors, history, removeError } = this.props;

        if (errors.message === "Sorry, that username and/or email is taken"
            && signUp == undefined) {
            removeError();
        }

        return (
            <div className="flex min-h-full items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
                <div className="w-full max-w-md space-y-8">
                    <div>
                        <img
                            className="mx-auto h-12 w-auto"
                            src="https://tailwindui.com/img/logos/mark.svg?color=indigo&shade=600"
                            alt="Your Company"
                        />
                        <h2 className="mt-6 text-center text-3xl font-bold tracking-tight text-gray-900">
                            {heading}
                        </h2>
                        {/* <p className="mt-2 text-center text-sm text-gray-600">
                            Or{' '}
                            <a href="#" className="font-medium text-indigo-600 hover:text-indigo-500">
                                start your 14-day free trial
                            </a>
                        </p> */}
                    </div>

                    <form className="mt-8 space-y-6" onSubmit={this.handleSubmit}>
                        {/* <input type="hidden" name="remember" defaultValue="true" /> */}
                        <div className="-space-y-px rounded-md shadow-sm">

                            
                            <div>
                                <label htmlFor="email-address" className="sr-only">
                                    Email address
                                </label>
                                <input
                                    id="email-address"
                                    name="email"
                                    type="email"
                                    autoComplete="email"
                                    required
                                    className="relative block w-full rounded-t-md border-0 py-1.5 text-gray-900 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:z-10 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                                    placeholder="Email address"
                                />
                            </div>
                            <div>
                                <label htmlFor="password" className="sr-only">
                                    Password
                                </label>
                                <input
                                    id="password"
                                    name="password"
                                    type="password"
                                    autoComplete="current-password"
                                    required
                                    className="relative block w-full rounded-b-md border-0 py-1.5 text-gray-900 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:z-10 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                                    placeholder="Password"
                                />
                            </div>
                        </div>

                        {/* <div className="flex items-center justify-between">
                            <div className="flex items-center">
                                <input
                                    id="remember-me"
                                    name="remember-me"
                                    type="checkbox"
                                    className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-600"
                                />
                                <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-900">
                                    Remember me
                                </label>
                            </div>

                            <div className="text-sm">
                                <a href="#" className="font-medium text-indigo-600 hover:text-indigo-500">
                                    Forgot your password?
                                </a>
                            </div>
                        </div> */}

                        <div>
                            <button
                                type="submit"
                                className="group relative flex w-full justify-center rounded-md bg-indigo-600 py-2 px-3 text-sm font-semibold text-white hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
                            >
                                Sign in
                            </button>
                        </div>
                    </form>
                </div>
            </div>
            // </>

            // <div>
            //     <div>
            //         <div>
            //             <form onSubmit={this.handleSubmit}>
            //                 <h2>{heading}</h2>
            //                 {errors.message && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">{errors.message}</div>}
            //                 <label htmlFor="email">email:</label>
            //                 <input
            //                     id="email"
            //                     name="email"
            //                     onChange={this.handleChange}
            //                     value={email}
            //                     type="text"
            //                 />

            //                 <label htmlFor="password">password:</label>
            //                 <input
            //                     id="password"
            //                     name="password"
            //                     onChange={this.handleChange}
            //                     type="password"
            //                 />
            //                 {signUp && (
            //                     <div>
            //                         <label htmlFor="username">username:</label>
            //                         <input
            //                             id="username"
            //                             name="username"
            //                             onChange={this.handleChange}
            //                             value={username}
            //                             type="text"
            //                         />

            //                         <label htmlFor="image-url">image URL:</label>
            //                         <input
            //                             id="image-url"
            //                             name="profileImageUrl"
            //                             onChange={this.handleChange}
            //                             value={profileImageUrl}
            //                             type="text"
            //                         />
            //                     </div>
            //                 )}
            //                 <button className="text-sm font-semibold leading-6 text-gray-900" type="submit">
            //                     {buttonText}
            //                 </button>
            //             </form>
            //         </div>
            //     </div>
            // </div >
        )
    }
}
